"use strict";
(function(n) {
  n("a.page-scroll").bind("click", function(t) {
    var i = n(this);
    n("html, body").stop().animate({
      scrollTop: n(i.attr("href")).offset().top - 50
    }, 1250, "easeInOutExpo");
    t.preventDefault()
  });
  ScrollPosStyler.init({
    spsClass: 'navbar'
  });
  n(".navbar-toggler").bind("click", function(t) {
    if(!window.scrollY) {
      n("nav").toggleClass("sps--blw");
      n("nav").toggleClass("sps--abv");
    }
  });
  window.sr = ScrollReveal();
  sr.reveal(".sr-icons", {
    duration: 600,
    scale: .3,
    distance: "0px"
  }, 200);
  sr.reveal(".sr-button", {
    duration: 1e3,
    delay: 200
  });
  sr.reveal(".sr-contact", {
    duration: 600,
    scale: .3,
    distance: "0px"
  }, 300);
  n("a.see-more").bind("click", function(t) {
    var i = n(n(this).attr("href"));
    i != null && (i.css("display") == "none" ? i.removeClass("d-none") : i.addClass("d-none"));
    t.preventDefault()
  });
})(jQuery);
