ScrollPosStyler.init({
  spsClass: 'navbar'
});

window.sr = ScrollReveal();

sr.reveal('.sr-icons', {
  duration: 600,
  scale: .3,
  distance: '0px'
}, 200);

sr.reveal('.sr-button', {
  duration: 1e3,
  delay: 200
});

sr.reveal('.sr-contact', {
  duration: 600,
  scale: .3,
  distance: '0px'
}, 300);

$('.navbar-toggler').bind('click', function(t) {
  if(!window.scrollY) {
    $('nav').toggleClass('sps--blw');
    $('nav').toggleClass('sps--abv');
  }
});

$('a.page-scroll').bind('click', function(t) {
  var i = n(this);
  $('html, body').stop().animate({
    scrollTop: n(i.attr('href')).offset().top - 50
  }, 1250, 'easeInOutExpo');
  t.preventDefault()
});

$('a.see-more').bind('click', function(t) {
  var i = n(n(this).attr('href'));
  i != null && (i.css('display') == 'none' ? i.removeClass('d-none') : i.addClass('d-none'));
  t.preventDefault()
});
